import React, { useState } from 'react'
import { Paper } from "@material-ui/core/";

import DateFnsUtils from "@date-io/date-fns";
import JalaliUtils from "@date-io/jalaali";

import { BasePicker, MuiPickersUtilsProvider, TimePickerView, Calendar } from "material-ui-pickers";


//import { cloneCrossUtils } from "utils/helpers";


function IrisaDialogDatePicker(props){

    const [selectedDate, handleDateChange] = useState(new Date());
    const [startDate,setStartDate] = useState(null)
    const [endDate,setEndDate] = useState(null)
    const [nchange,setNchange] = useState(0)

    function handleTextFieldChange1(event){
      const {name,value} = event.target
      console.log(name,value)
      //handleDateChange(value)
    }

    return (
<MuiPickersUtilsProvider utils={JalaliUtils}>
      <BasePicker value={selectedDate}   onChange={e=>{console.log("outside",e.target)}}      
      >
        {({
          date,
          handleAccept,
          handleChange,
          handleClear,
          handleDismiss,
          handleSetTodayDate,
          handleTextFieldChange,
          pick12hOr24hFormat,
        }) => (
          <div>
            <div className="picker">
              <Paper style={{ overflow: "hidden" }}>
                <p>Number of changes : {nchange}- from {startDate} to {endDate}</p>
                <Calendar date={date} 
                allowKeyboardControl={true}
                onChange={e=>{
                  console.log("inside",e);
                  setNchange(nchange+1)
                  if(nchange%2===1){
                    setStartDate(e._d)
                  }
                  else{
                    setEndDate(e._d)
                  }
                }}
                onInputChange={e=>{console.log("onInputChange",e.target)}}
                labelFunc={(date,invalidLabel)=>{
                  console.log(`labelFunc => ${date} - ${invalidLabel}`)
                  return 'test '
                }}
                />
              </Paper>
            </div>

            <TimePickerView
              type="hours"
              date={date}
              ampm={false}
              onMinutesChange={() => {}}
              onSecondsChange={() => {}}
              onHourChange={handleChange}
            />
          </div>
        )}
      </BasePicker>
    </MuiPickersUtilsProvider>
    )
}

export default IrisaDialogDatePicker
