import ReactDOM from "react-dom";
import React, { useState } from "react";
//import Typography from "@material-ui/core/Typography";

import PropTypes from "prop-types";
import clsx from "clsx";

import {
  isValid,
  format,
  isSameDay,
  startOfWeek,
  endOfWeek,
  isWithinInterval
} from "date-fns";

import {
  DatePicker,
  MuiPickersUtilsProvider,
  TimePickerView,
  Calendar
} from "material-ui-pickers";
import { IconButton, withStyles } from "@material-ui/core";

import DateFnsUtils from "@date-io/date-fns";
import JalaliUtils from "@date-io/jalaali";
import { createMuiTheme, responsiveFontSizes } from "@material-ui/core/styles";
import Checkbox from "@material-ui/core/Checkbox";
import { endOfDay } from "date-fns/esm/fp";

//import { cloneCrossUtils } from "utils";
//import MomentUtils from '@date-io/moment';

const styles = theme => ({
  dayWrapper: {
    position: "relative"
  },
  day: {
    width: 36,
    height: 36,
    fontSize: theme.typography.caption.fontSize,
    margin: "0 2px",
    color: "inherit"
  },
  customDayHighlight: {
    position: "absolute",
    top: 0,
    bottom: 0,
    left: "2px",
    right: "2px",
    border: `1px solid ${theme.palette.secondary.main}`,
    borderRadius: "50%"
  },
  nonCurrentMonthDay: {
    color: theme.palette.text.disabled
  },
  highlightNonCurrentMonthDay: {
    color: "#676767"
  },
  highlight: {
    background: theme.palette.primary.main,
    color: theme.palette.common.white
  },
  firstHighlight: {
    extend: "highlight",
    borderTopLeftRadius: "50%",
    borderBottomLeftRadius: "50%"
  },
  endHighlight: {
    extend: "highlight",
    borderTopRightRadius: "50%",
    borderBottomRightRadius: "50%"
  }
});

function App(props) {
  /*
    const dt = new Date('2019/08/07')
    const start = startOfWeek(dt)
    const end = endOfWeek(dt)
    let cloneDate=null
    */

  let theme = createMuiTheme();
  //theme = responsiveFontSizes(theme);
  //console.log(theme.typography.caption.fontSize)
  //const classes = styles(theme);

  const classes = styles(theme);

  //console.log("classes--->", classes);

  const [chk, setChk] = useState(false);

  function cloneCrossUtils(dat) {
    return dat;
  }

  const formatWeekSelectLabel = (date, invalidLabel) => {
    console.log("formatWeekSelectLabel --> start");
    let dateClone = cloneCrossUtils(date);

    return dateClone && isValid(dateClone)
      ? `Week of ${format(startOfWeek(dateClone), "MMM do")}`
      : invalidLabel;
  };

  const renderWrappedWeekDay = (date, selectedDate, dayInCurrentMonth,dayComponent) => {
    let dateClone = cloneCrossUtils(date);
    let selectedDateClone = cloneCrossUtils(selectedDate);

    const start = startOfWeek(selectedDateClone);
    const end = endOfWeek(selectedDateClone);

    const dayIsBetween = isWithinInterval(dateClone, { start, end });
    const isFirstDay = isSameDay(dateClone, start);
    const isLastDay = isSameDay(dateClone, end);

    //console.log("renderWrappedWeekDay ---> start");

    const wrapperClassName = clsx({
      [classes.highlight]: dayIsBetween,
      [classes.firstHighlight]: isFirstDay,
      [classes.endHighlight]: isLastDay
    });

    const dayClassName = clsx(classes.day, {
      [classes.nonCurrentMonthDay]: !dayInCurrentMonth,
      [classes.highlightNonCurrentMonthDay]: !dayInCurrentMonth && dayIsBetween
    });

    console.log("renderWrappedWeekDay ---> ",dayComponent);

    return (
      <div className={wrapperClassName}>
        <IconButton className={dayClassName}>
          <span> d </span>
        </IconButton>
      </div>
    );
  };

  return (
    <div className={classes}>
      <Checkbox checked={chk} onChange={e => setChk(e.target.checked)} />
      <br />
      <MuiPickersUtilsProvider utils={JalaliUtils} local={"fa"}>
        <DatePicker
          className={classes}
          renderDay={chk ? renderWrappedWeekDay : null}
          //labelFunc={chk ? formatWeekSelectLabel : null}
          //labelFunc={date => (date ? date.format("jYYYY/jMM/jDD hh:mm A") : "")}

          onChange={e => {
            console.log(e);
          }}
        />
      </MuiPickersUtilsProvider>
    </div>
  );
}

export default App;

//ReactDOM.render(<App />, document.getElementById("root"));
