import React, { useState } from 'react'
import { Paper } from "@material-ui/core/";

import DateFnsUtils from "@date-io/date-fns";
import JalaliUtils from "@date-io/jalaali";
import IconButton from '@material-ui/core/IconButton';
import { BasePicker, MuiPickersUtilsProvider, TimePickerView, Calendar } from "material-ui-pickers";
//import moment from "moment"
import jMoment from "moment-jalaali";

//import { cloneCrossUtils } from "utils/helpers";
import {isValid,format,isSameDay,startOfWeek,endOfWeek,isWithinInterval} from "date-fns";
import MomentUtils from '@date-io/moment';

jMoment.loadPersian({ dialect: "persian-modern", usePersianDigits: true });

const styles = theme => ({
  dayWrapper: {
    position: "relative",
  },
  day: {
    width: 36,
    height: 36,
    fontSize: theme.typography.caption.fontSize,
    margin: "0 2px",
    color: "inherit",
  },
  customDayHighlight: {
    position: "absolute",
    top: 0,
    bottom: 0,
    left: "2px",
    right: "2px",
    border: `1px solid ${theme.palette.secondary.main}`,
    borderRadius: "50%",
  },
  nonCurrentMonthDay: {
    color: theme.palette.text.disabled,
  },
  highlightNonCurrentMonthDay: {
    color: "#676767",
  },
  highlight: {
    background: theme.palette.primary.main,
    color: theme.palette.common.white,
  },
  firstHighlight: {
    extend: "highlight",
    borderTopLeftRadius: "50%",
    borderBottomLeftRadius: "50%",
  },
  endHighlight: {
    extend: "highlight",
    borderTopRightRadius: "50%",
    borderBottomRightRadius: "50%",
  },
});

function IrisaDialogDatePicker(props){

    const [selectedDate, handleDateChange] = useState(new Date());
    const [startDate,setStartDate] = useState(null)
    const [endDate,setEndDate] = useState(null)
    const [nchange,setNchange] = useState(0)

    function handleTextFieldChange1(event){
      const {name,value} = event.target
      console.log(name,value)
      //handleDateChange(value)
    }

    return (
<MuiPickersUtilsProvider utils={JalaliUtils} local={"fa"}>
      <BasePicker value={selectedDate}   onChange={e=>{console.log("outside",e.target)}}      
      cancelLabel={"Cancel"}
      okLabel={"Ok"}
      showTodayButton={true}      
      >
        {({
          date,
          handleAccept,
          handleChange,
          handleClear,
          handleDismiss,
          handleSetTodayDate,
          handleTextFieldChange,
          pick12hOr24hFormat,
        }) => (
          <div>
            <div className="picker">
              <Paper style={{ overflow: "hidden" }}>

                <p>
                  Number of changes : {nchange}  
                  - from: {String(jMoment(startDate,'YYYY-MM-DD'))} 
                  - to: {String(jMoment(endDate,'jYYYY-jMM-jDD'))}
                </p>
                <Calendar date={date}  
                allowKeyboardControl={true}                
                format={"YYYY/MM/DD"}
                renderDay={(date, selectedDate, dayInCurrentMonth,dayComponent)=>{
                  //const { classes } = this.props;
                  //let dateClone = date;//cloneCrossUtils(date);
                  //let selectedDateClone = selectedDate;//cloneCrossUtils(selectedDate);

                  //const start = startDate;//startOfWeek(selectedDateClone);
                  //const end = endDate;//endOfWeek(selectedDateClone);
              /*
                  const dayIsBetween = isWithinInterval(dateClone, { start, end });
                  const isFirstDay = isSameDay(dateClone, start);
                  const isLastDay = isSameDay(dateClone, end);

                  const wrapperClassName = clsx({
                    [classes.highlight]: dayIsBetween,
                    [classes.firstHighlight]: isFirstDay,
                    [classes.endHighlight]: isLastDay,
                  });

                  const dayClassName = clsx(classes.day, {
                    [classes.nonCurrentMonthDay]: !dayInCurrentMonth,
                    [classes.highlightNonCurrentMonthDay]: !dayInCurrentMonth && dayIsBetween,
                  });
                  */
              //console.log('e-->',(startDate))
              return (
                <div >
                    {

                        (format(startDate,'d') <=dayComponent.props.children && dayComponent.props.children<=format(endDate,'d')) ?
                        <span style={{background:'#ff0099'}} >{dayComponent}</span>  :
                        <span>{dayComponent}</span>                       

            //            mdays.reduce((s,itm)=>{return dayComponent.props.children===format(itm,'d') && (s || itm)},false) ?
            //                <span>{dayComponent}</span> : <span style={{background:'#ff0099'}} >{dayComponent}</span>

                    }
                </div>);
                }}
                labelFunc={(date, invalidLabel) => {
                  console.log(date)
                  let dateClone = date;//cloneCrossUtils(date);

                  return dateClone && isValid(dateClone)
                    ? `Week of ${format(startOfWeek(dateClone), "MMM do")}`
                    : invalidLabel;
                }}                
                onChange={e=>{
                  //console.log("inside",e);
                  setNchange(nchange+1)
                  if(nchange%2===1){
                    setStartDate(e._d)
                  }
                  else{
                    setEndDate(e._d)
                  }
                  console.log(e._d.getFullYear(),
                  e._d.getMonth(),e._d.getDate());
                }}
                onInputChange={e=>{console.log("onInputChange",e.target)}}
                // labelFunc={(date,invalidLabel)=>{
                //   return (new Date(moment())).format('YYYY/MM/DD') 
                // }}
                />
              </Paper>
            </div>

            <TimePickerView
              type="hours"
              date={date}
              ampm={false}
              onMinutesChange={() => {}}
              onSecondsChange={() => {}}
              onHourChange={handleChange}
            />
          </div>
        )}
      </BasePicker>
    </MuiPickersUtilsProvider>
    )
}

export default IrisaDialogDatePicker
