import React,{useState} from 'react';
import {Dialog,DialogActions,DialogContent,DialogContentText,DialogTitle,
    Select,MenuItem,Grid,Button,List,TextField,Input,InputAdornment} from '@material-ui/core';
import {Person} from '@material-ui/'
function UsersGroupsApprolesSearch(props){
    const {open} = props;

    const [selectedValueToSearch,setSelectedValueToSearch] = useState(false);
    const [valueToSearch,setValueToSearch] = useState("");
    const [firstName,setFirstName] = useState("");
    const [lastName,setLastName] = useState("");
    const [email,setEmail] = useState("");
    const [ID,setID] = useState("");

    return (
        <Dialog open={open}>
            <DialogTitle>جستجوی کاربران گروهها و نقش ها</DialogTitle>
            <DialogContent>
                <DialogContentText>کاربر می تواند جستجو کند</DialogContentText>
                <Grid container>
                    <Grid item xs={6}>
                        <Select value={selectedValueToSearch} onChange={e=>{setSelectedValueToSearch(e.target.value)}}>
                        <MenuItem value={"all"}>All</MenuItem>
                        <MenuItem value={"users"}>Users</MenuItem>
                        <MenuItem value={"groups"}>Groups</MenuItem>
                        <MenuItem value={"applicationRole"}>Application Role</MenuItem>
                        </Select>
                    </Grid>
                    <Input
                    value={valueToSearch}
                    label={"serach use , groups , ..."}
                    endAdornment={
                        <InputAdornment position="end">
                            <Person />
                        </InputAdornment>
                    }
                    />
                    {/* <Select >
                        <List><TextField label="نام" value={firstName}/></List>
                        <List><TextField label="نام خانوادگی" value={lastName}/></List>
                        <List><TextField label="ایمیل" value={email}/></List>
                        <List><TextField label="شناسه جستجو" value={ID}/></List>
                    </Select> */}
                    <Grid item xs={6}>
                        <Button onClick={e=>{}}>جستجو</Button>
                    </Grid>
                </Grid>
            </DialogContent>
        </Dialog>
    );
}

export default UsersGroupsApprolesSearch;
