import React, {lazy, Suspense,useState} from 'react';
import logo from './logo.svg';
import './App.css';
import {Router, Route} from "react-router-dom";
import MenuAppBar from "./Components/AppBar"
import {createBrowserHistory} from 'history';
import {LoginProvider} from "./Utils/StateManagement";
//import App1 from './irisacomponents/src/AppMain'
//import App1 from './Components/IrisaComponents/TextField/FormTextField'
//import App1 from './Components/IrisaComponents/Outcome/FormOutcome'
//import App1 from './Components/IrisaComponents/Button/FormButton'
//import {Grid,Card} from '@material-ui/core';
import UsersGroupsApprolesSearch from './Components/IrisaComponents/UsersGroupsApprolesSearch';

const history = createBrowserHistory();
const Index = lazy(() => import('./Components/Index'));
const Users = lazy(() => import('./Components/Users'));
const Home = lazy(() => import('./Components/Home'));

/**
 * Main File
 * @returns {*}
 * @constructor
 */
function App() {

    /**
     * initial first states for authentication
     * @type {{authenticated: boolean}}
     */
    const initialAuthState = {
        authenticated: false
    };

    const [isOpen,setIsOpen] = useState(false);


    const TestComp =()=> (                                   
        <div>
            <button onClick={e=>{setIsOpen(true)}}>Open Dialog</button>
            <UsersGroupsApprolesSearch open={isOpen} data={[
                  {"roleName":"LAB_Lubricant_PRJ.Reception","members":"z.rahimi, b.ghazi","description":"desc 1","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"HSE_Spraying_PRJ.Applicants","members":"","description":"desc 2","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"HSE_Spraying_PRJ.HExpert","members":"","description":"desc 3","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"LAB_Lubricant_PRJ.Reception2","members":"z.rahimi, b.ghazi","description":"desc 1","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"HSE_Spraying_PRJ.Applicants2","members":"","description":"desc 2","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"HSE_Spraying_PRJ.HExpert2","members":"","description":"desc 3","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"LAB_Lubricant_PRJ.Reception3","members":"z.rahimi, b.ghazi","description":"desc 1","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"HSE_Spraying_PRJ.Applicants3","members":"","description":"desc 2","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"HSE_Spraying_PRJ.HExpert3","members":"","description":"desc 3","context":"OracleBPMProcessRolesApp"},
                  {"roleName":"LAB_Lubricant_PRJ.Reception4","members":"105381, 78940, 90007","description":"desc 4","context":"OracleBPMProcessRolesApp"}
              ]}/>
        </div> 
    );

    /**
     * create Reducer for authentication context
     * @param state
     * @param action
     * @returns {*}
     * @constructor
     */
    const AuthenticationReducer = (state, action) => {
        switch (action.type) {
            case 'changeAuthenticate':
                return {
                    ...state,
                    authenticated: action.authenticated
                };

            default:
                return state;
        }
    };

    return (
        // <div className="App">
            <LoginProvider
                initialState={initialAuthState}
                reducer={AuthenticationReducer}>
                <Router history={history}>
                    <MenuAppBar history={history}/>
                    <header className="App-header">
                        <img src={logo} className="App-logo" alt="logo"/>
                        <p>
                            Edit <code>src/App.js</code> and save to reload.
                        </p>
                        <div>
                            <Suspense fallback={<div>Loading...</div>}>
                                <Route path="/" exact component={Index}/>
                                <Route path="/about/" component={Home}/>
                                <Route path="/testcomp/" component={                                    
                                    TestComp 
                                }/>
                                <Route path="/users/" component={Users}/>
                            </Suspense>
                        </div>
                    </header>
                </Router>
            </LoginProvider>
        // </div>
    );
}

export default App;
