import React,{useState, useEffect} from 'react';
import {Dialog,DialogActions,DialogContent,DialogContentText,DialogTitle,
    Select,MenuItem,Grid,Button,List,TextField,Input,InputAdornment,IconButton,ListItem,
    Table,TableBody,TableCell,TableHead,TablePagination,TableRow,TableSortLabel,TableFooter} from '@material-ui/core';

import {Person,Search, CheckBox} from '@material-ui/icons';

function UsersGroupsApprolesSearch(props){
    const {open,data} = props;

    const [selectedValueToSearch,setSelectedValueToSearch] = useState("all");
    const [valueToSearch,setValueToSearch] = useState("");
    const [firstName,setFirstName] = useState("");
    const [lastName,setLastName] = useState("");
    const [email,setEmail] = useState("");
    const [ID,setID] = useState("");
    const [isShowList,setisShowList] = useState(false);
    const [rowsPerPage,setRowsPerPage] = useState(5);
    const [page,setPage] = useState(0);
    const [chk,setChk] = useState(false);


    useEffect(()=>{
        if(firstName.length===0 && lastName.length===0 && email.length===0 && ID.length===0)
            setValueToSearch("")
        else
            setValueToSearch(`${firstName} , ${lastName} , ${email} , ${ID}`)                
    },[firstName,lastName,email,ID])    

    //function makeSearchCondition()//(ifname,ilname,iemail,iid){
        //return `${ifname} , ${ilname} , ${iemail} , ${iid}`;
    //    return `${firstName} , ${lastName} , ${email} , ${ID}`;
    //}

    function resetFields(){
        setFirstName("")
        setLastName("")
        setEmail("")
        setID("")
    }

    return (
        <div>
        <Dialog open={open} fullWidth={true} maxWidth={'xl'}>
            <DialogTitle>جستجوی کاربران گروهها و نقش ها</DialogTitle>
            <DialogContent>
                {/* <DialogContentText>کاربر می تواند جستجو کند</DialogContentText> */}
                <Grid container spacing={0}>
                    <Grid item xs={4}>
                        <Select 
                        value={selectedValueToSearch} 
                        autoWidth={true}
                        onChange={e=>{setSelectedValueToSearch(e.target.value)}}>
                        <MenuItem value={"all"}>All</MenuItem>
                        <MenuItem value={"users"}>Users</MenuItem>
                        <MenuItem value={"groups"}>Groups</MenuItem>
                        <MenuItem value={"applicationRole"}>Application Role</MenuItem>
                        </Select>
                    </Grid>

                    <Grid item xs={6}>
                    <Input
                    value={valueToSearch}
                    fullWidth={true}
                    label={"serach use , groups , ..."}
                    onChange={e=>{setValueToSearch(e.target.value);}}
                    endAdornment={
                        <InputAdornment position="end">
                            <IconButton onClick={e=>{setisShowList(true);}}><Search/></IconButton>
                        </InputAdornment>
                    }
                    />
                    </Grid>

                    <Grid item xs={1}>
                        <Button onClick={e=>{}}>جستجو</Button>
                    </Grid>
                    <Grid item xs={1}>
                        <Button onClick={e=>{resetFields()}}>ریست</Button>
                    </Grid>                    
                <Grid item xs={12}>
                    <Table>
                        <TableHead>
                            <TableCell align="right">Context</TableCell>  
                            <TableCell align="right">Description</TableCell>
                            <TableCell align="right">Members</TableCell>
                            <TableCell align="right">Name</TableCell>
                            <TableCell>Select</TableCell>
                        </TableHead>
                        <TableBody>
                        {   data!== undefined &&
                            data.slice(page*rowsPerPage,page*rowsPerPage+rowsPerPage).map(row=>{return (
                            <TableRow key={row.roleName}>
                                <TableCell align="right">{row.context}</TableCell>      
                                <TableCell align="right">{row.description}</TableCell>        
                                <TableCell align="right">{row.members}</TableCell>        
                                <TableCell component="th" scope="row" align="right">{row.roleName}</TableCell>        
                                <TableCell align="right"><CheckBox value={chk} onChange={e=>{setChk(e.target.checked);console.log(e.target.checked);}} /></TableCell>        
                            </TableRow>)})
                        }
                        </TableBody>   
                        <TableFooter>
                            <TableRow>
                                <TablePagination
                                    rowsPerPageOptions={[5, 10, 25]}
                                    colSpan={3}
                                    count={data.length}
                                    rowsPerPage={rowsPerPage}
                                    page={page}
                                    SelectProps={{
                                        native: true,
                                      }}
                                    onChangePage={(e,p)=>{setPage(p)}}
                                    onChangeRowsPerPage={e=>{setRowsPerPage(e.target.value)}}
                                />
                            </TableRow>       
                        </TableFooter> 
                    </Table>
                    </Grid>
                    <Grid item >
                        Selected Items:
                        <CheckBox value={"chk"} checked={chk} on onChange={e=>{setChk(!chk);console.log(e.target);}} />
                    </Grid>

                    </Grid> 

            </DialogContent>
        </Dialog>
        <Dialog open={isShowList}>
            <List>
                <ListItem><TextField label="نام" value={firstName} onChange={e=>setFirstName(e.target.value)}/></ListItem>
                <ListItem><TextField label="نام خانوادگی" value={lastName}  onChange={e=>setLastName(e.target.value)}/></ListItem>
                <ListItem><TextField label="ایمیل" value={email}  onChange={e=>setEmail(e.target.value)}/></ListItem>
                <ListItem><TextField label="شناسه جستجو" value={ID}  onChange={e=>setID(e.target.value)}/></ListItem>
                <ListItem><Button onClick={e=>{
                        setisShowList(false)
                        resetFields();
                    }}
                    >انصراف</Button> 
                    <Button onClick={e=>{
                        //setValueToSearch("sdff sdfdsfdsf fsdfsddsf");
                         //firstName+"-"+lastName+"-"+email+"-"+ID 
                        setisShowList(false) }}>تایید</Button>                               
                </ListItem>    
            </List>            
        </Dialog>                        
        </div>
    );
}

export default UsersGroupsApprolesSearch;
