/* eslint-disable no-console */

import React,{useState} from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import TableCell from '@material-ui/core/TableCell';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import Paper from '@material-ui/core/Paper';
import { AutoSizer, Column, SortDirection, Table,List } from 'react-virtualized';

const styles = theme => ({
  table: {
    fontFamily: theme.typography.fontFamily,
  },
  flexContainer: {
    display: 'flex',
    alignItems: 'center',
    boxSizing: 'border-box',
  },
  tableRow: {
    cursor: 'pointer',
  },
  tableRowHover: {
    '&:hover': {
      backgroundColor: theme.palette.grey[200],
    },
  },
  tableCell: {
    flex: 1,
  },
  noClick: {
    cursor: 'initial',
  },
});

function ReactVirtualizedTable(props) {
  const [width,setWidth] = useState(400)
  const [height,setHeight] = useState(400)

  const getRowClassName = ({ index }) => {
    const { classes, rowClassName, onRowClick } = this.props;

    return classNames(classes.tableRow, classes.flexContainer, rowClassName, {
      [classes.tableRowHover]: index !== -1 && onRowClick != null,
    });
  };



  return (
    // <ul>
    //   {
    //     [...Array(1000000).keys()].map(item=>(
    //       <li key={item}>Row {item}</li>
    //     ))
    //   }
    // </ul>
    <AutoSizer>
      {
        ({height,width})=>(
          <Table
          height={height}
          itemCount={1000}
          itemSize={35}
          width={width}
          >
            {[...Array(100000).keys()].map(item=>(<label key={item}>Row {item}</label>))}
          </Table>
        )
      }
    </AutoSizer>
  );
}

export default ReactVirtualizedTable;
